						["custom"] = "function(event,...)\n    \n    if(event == \"ENCOUNTER_START\")then\n        \n        if(not aura_env.initialized)then\n            aura_env.readRotationFromExrtNotes();\n        end\n        \n        aura_env.vitaCounter = 0;\n        aura_env.lastVita = -999;\n        aura_env.assignedBackups = {};\n        \n        local difficultyID = select(3,...);\n        if (difficultyID == 0 or difficultyID == 16)then\n            aura_env.duration = 6;\n        end\n        \n        \n    elseif (event == \"READY_CHECK\") then\n        \n        aura_env.readRotationFromExrtNotes();\n        \n    elseif (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and aura_env.initialized) then\n        \n        local time,subevent,_,_,_,_,_,_,destName,_,_,spellId = ... \n        if subevent == \"SPELL_AURA_APPLIED\" then\n            if(spellId == aura_env.trackedSpell)then\n                \n                if(aura_env.lastVita + 10 < time)then\n                    --vita reset\n                    aura_env.stacks = aura_env.maxStacks;\n                    aura_env.isInCurrentVita = false;\n                    aura_env.vitaCounter = aura_env.vitaCounter + 1;\n                    aura_env.lastSent = nil;\n                else\n                    --stack decrese\n                    aura_env.stacks =  aura_env.stacks - 1; \n                end\n                \n                aura_env.lastVita = time;\n                \n                --check rotation\n                aura_env.holder = destName;\n                aura_env.checkRotation(aura_env.vitaCounter);\n                \n                local vitaRunners = {};\n                local runner = \"\";\n                for i=aura_env.stacks-1,0,-1 do\n                    runner = aura_env.getAssginedPlayer(aura_env.vitaCounter,aura_env.maxStacks-i);\n                    table.insert(vitaRunners,runner);\n                    if(runner == WeakAuras.me)then\n                        aura_env.isInCurrentVita = true;\n                    end\n                    if(aura_env.vitaRotations[aura_env.vitaCounter] and aura_env.vitaRotations[aura_env.vitaCounter][aura_env.maxStacks-i] == WeakAuras.me)then\n                        aura_env.isInCurrentVita = true;\n                    end\n                end\n                \n                WeakAuras.ScanEvents(\"WA_RADEN_NIGHTMARE_UPDATE\",vitaRunners,aura_env.holder);\n                \n                return true;\n                \n            elseif(spellId == aura_env.instabilitySpell and aura_env.stacks == 1)then\n                if(not aura_env.lastSent)then\n                    aura_env.lastSent = true;\n                    WeakAuras.ScanEvents(\"WA_RADEN_NIGHTMARE_UPDATE\",{},nil);\n                end\n            end\n        end\n    elseif(event == \"GROUP_ROSTER_UPDATE\")then\n        aura_env.initialized = false;\n    end\nend\n\n\n\n",
