						["custom"] = "function(allStates, event, ...)\n    allStates = allStates or {}\n    if event == \"UNIT_POWER_UPDATE\" then\n        local source, type = ...\n        if source == \"player\" and type == \"COMBO_POINTS\" then\n            aura_env.power, aura_env.maxPower = UnitPower(\"player\", Enum.PowerType.ComboPoints), UnitPowerMax(\"player\", Enum.PowerType.ComboPoints)\n            for i = 1,aura_env.maxPower do\n                if i <= aura_env.power then\n                    allStates[i].value = 1\n                else\n                    allStates[i].value = 0 \n                end\n                allStates[i].changed = true\n            end\n        end\n        \n    elseif event == \"PLAYER_ENTERING_WORLD\" or event == \"UNIT_MAXPOWER\" then\n        if allStates then\n            for k,v in pairs(allStates) do\n                v.show = false;\n                v.changed = true;\n            end\n        end\n        aura_env.power, aura_env.maxPower = UnitPower(\"player\", Enum.PowerType.ComboPoints), UnitPowerMax(\"player\", Enum.PowerType.ComboPoints)\n        --print(aura_env.maxPower)\n        for i = 1,aura_env.maxPower do\n            allStates[i] = {\n                changed = true,\n                show = true,\n                progressType = \"static\",\n                value = 0,\n                total = 1,\n                index = i\n            }\n            if i <= aura_env.power then\n                allStates[i].value = 1\n            else\n                allStates[i].value = 0 \n            end\n            allStates[i].changed = true\n        end\n    end\n    --aura_env.maxCP = #allStates ~= 0 and #allStates or 5\n    return true\nend",
