					["custom"] = "aura_env.powerCasts = {-- generation, Blessing of Elune, Celestial Alignment, Incarnation\n    [GetSpellInfo(202767)] = {10}, --New Moon\n    [GetSpellInfo(202768)] = {20}, --Half Moon\n    [GetSpellInfo(202771)] = {40}, --Full Moon\n    [GetSpellInfo(194153)] = {12}, --Lunar Strike\n    [GetSpellInfo(190984)] = {8}, --Solar Wrath\n    [GetSpellInfo(202347)] = {12} -- Stellar Flare\n}\n\naura_env.getPowerFromCast = function()\n    local cast = UnitCastingInfo(\"player\") or UnitChannelInfo(\"player\") or nil\n    if not cast then\n        return 0\n    end\n    local cost = GetSpellPowerCost(cast)\n    if cost and cost[1] and cost[1].name == aura_env.powerToken then\n        return -cost[1].cost\n    end\n    --print(cast)\n    local data = aura_env.powerCasts[cast] or nil\n    if not data then\n        return 0\n    end\n    --print(data[1], GetTime())\n    local power = data[1] or 0 \n    return power\nend\n\naura_env.powerColours = {\n    [\"RAGE\"] = {\n        r = 255/255,\n        g = 0/255,\n        b = 0/255,\n        a = 1,\n        rbg = 65/255,\n        gbg = 0/255,\n        bbg = 0/255,\n        abg = 165.75/255,\n    },\n    [\"ENERGY\"] = {\n        r = 255/255,\n        g = 255/255,\n        b = 0/255,\n        a = 1,\n        rbg = 65/255,\n        gbg = 65/255,\n        bbg = 0/255,\n        abg = 165.75/255,\n    },\n    [\"LUNAR_POWER\"] = {\n        r = 77/255,\n        g = 133/255,\n        b = 230/255,\n        a = 1,\n        rbg = 22/255,\n        gbg = 35/255,\n        bbg = 64/255,\n        abg = 165.75/255,\n    },\n    [\"MANA\"] = {    \n        r = 0/255,\n        g = 170/255,\n        b = 255/255,\n        a = 1,\n        rbg = 0/255,\n        gbg = 50/255,\n        bbg = 75/255,\n        abg = 165.75/255,\n    },\n}\naura_env.power = UnitPower(\"player\")\naura_env.powerMax = UnitPowerMax(\"player\")\naura_env.powerToken = select(2, UnitPowerType(\"player\"))\naura_env.castPower = 0\nlocal r = aura_env.region \nif not r.count then\n    r.count = r.bar:CreateFontString(nil, \"OVERLAY\")\n    r.count:SetJustifyH(\"CENTER\")\n    r.count:SetJustifyV(\"MIDDLE\")\n    r.count:SetPoint(\"CENTER\", r.bar, \"CENTER\", 0, 0)\n    r.count:SetFont(r.text:GetFont())\n    r.count:SetTextColor(r.text:GetTextColor())\n    r.count:Show()\n    r.count:SetFont(r.text:GetFont())\n    r.count:SetTextColor(r.text:GetTextColor())\n    r.count:SetShadowOffset(1, -1)\nend\naura_env.powerTextUpdate = function()\n    --print(1)\n    local a = aura_env\n    local r = a.region\n    --print(a.power)\n    if a.powerToken == \"MANA\" then \n        if a.power == a.powerMax then\n            return \"100%\"\n        else\n            return string.format(\"%.1f\", (a.power/a.powerMax)*100)..\"%\"\n        end\n    else\n        if a.castPower > 0 then\n            if a.power + a.castPower > a.powerMax then\n                return \"|cFFFF0000*|r\"..a.power..\"+\"..a.castPower..\"|cFFFF0000*|r\"\n            else\n                return a.power..\"+\"..a.castPower\n            end\n        else\n            return a.power\n        end\n    end\nend\naura_env.powerTextUpdate()",
