			["xOffset"] = -8.9998168945313,
			["preferToUpdate"] = false,
			["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then return end\n    local left,right,outsideLeft,outsideRight\n    local isOnLeft = aura_env.stacks and aura_env.stacks%2 == (aura_env.startLeft and 1 or 0)\n    if isOnLeft then\n        left,right = aura_env.stacks ,\"  --->\"\n    else\n        left,right = \"<---  \",aura_env.stacks\n    end  \n    \n    aura_env.region:SetOrientation(isOnLeft and \"HORIZONTAL\"or \"HORIZONTAL_INVERSE\")\n    \n    \n    outsideLeft,outsideRight = aura_env.buildNameLists(isOnLeft)\n    \n    return left,right,outsideLeft,outsideRight\nend\n\n\n",
			["yOffset"] = 90.000061035156,
			["anchorPoint"] = "CENTER",
			["sparkRotation"] = 0,
			["sparkRotationMode"] = "AUTO",
			["url"] = "https://wago.io/MethodRaden/7",
			["actions"] = {
				["start"] = {
					["sound"] = "Interface\\AddOns\\WeakAuras\\Media\\Sounds\\AirHorn.ogg",
					["do_sound"] = false,
				},
				["init"] = {
					["custom"] = "aura_env.debug = aura_env.config.debug\nif(aura_env.debug and EventFaker)then\n    aura_env.WA_GetUnitAura = EventFaker.GetUnitAura;\n    EventFaker.RegisterUnitAura(WA_GetUnitAura);\nelse\n    aura_env.WA_GetUnitAura = WA_GetUnitAura;\nend\n\naura_env.initialized = false;\naura_env.startLeft = aura_env.config.startLeft;\naura_env.trackedSpell = 306273;\naura_env.instabilitySpell = 306279;\naura_env.vitaRotations = {};\naura_env.vitaBackups = {};\naura_env.assignedBackups = {};\naura_env.lastVita = -999;\naura_env.maxStacks = 5;\n\nfunction aura_env.readRotationFromExrtNotes()    \n    local noteText = {strsplit(\"\\n\", VExRT.Note.Text1)}\n    local vitaIndex = 0;\n    local vitaBackupIndex = 0;\n    \n    aura_env.initialized = false;\n    \n    for _, line in ipairs(noteText) do                    \n        if string.sub(line, 1, 5) == \"#Vita\" then\n            vitaIndex = vitaIndex + 1\n            aura_env.vitaRotations[vitaIndex] = {}            \n            --only starting from xth char and whitespaces at end removed\n            local strippedLine = string.sub(line, 7):match(\"(.-)%s*$\")\n            --remove class coloring and find matches\n            for match in string.gmatch(strippedLine, \"|c%x%x%x%x%x%x%x%x(.-)|\") do\n                if UnitExists(match) or aura_env.debug then\n                    aura_env.vitaRotations[vitaIndex][#aura_env.vitaRotations[vitaIndex]+1] = match\n                else\n                    print(\"|cffff0000WA\"..aura_env.id..\"|r : Player \"..match..\" not in Raid. Please fix ExRT Note!\");\n                    return;\n                end\n            end\n        end\n        if string.sub(line, 1, 6) == \"#BVita\" then\n            vitaBackupIndex = vitaBackupIndex + 1\n            aura_env.vitaBackups[vitaBackupIndex] = {}            \n            --only starting from xth char and whitespaces at end removed\n            local strippedLine = string.sub(line, 8):match(\"(.-)%s*$\")\n            --remove class coloring and find matches\n            for match in string.gmatch(strippedLine, \"|c%x%x%x%x%x%x%x%x(.-)|\") do\n                if UnitExists(match) or aura_env.debug then\n                    aura_env.vitaBackups[vitaBackupIndex][#aura_env.vitaBackups[vitaBackupIndex]+1] = match\n                else\n                    print(\"|cffff0000WA\"..aura_env.id..\"|r : Player \"..match..\" not in Raid. Please fix ExRT Note!\");\n                    return;\n                end\n            end\n        end\n    end\n    \n    if(#aura_env.vitaRotations == 0)then\n        print(\"|cffff0000WA\"..aura_env.id..\"|r : '#Vita' rotations not assigned\");\n        return; \n    end\n    \n    for i,rotation in pairs(aura_env.vitaRotations) do\n        if(#rotation < 5)then\n            print(\"|cffff0000WA\"..aura_env.id..\"|r : Not enough people in '#Vita' rotation \".. i ..\".\");\n            return; \n        end\n    end\n    \n    for i,rotation in pairs(aura_env.vitaBackups) do\n        if(#rotation < 2)then\n            print(\"|cffff0000WA\"..aura_env.id..\"|r : Not enough people in '#BVita' rotation \".. i ..\".\");\n            return; \n        end\n    end\n    \n    print(\"|cff4384f5WA\"..aura_env.id..\" :|r Init successful.\");\n    \n    if(aura_env.config.isScripe or aura_env.debug)then\n        local display;\n        for i,rotation in pairs(aura_env.vitaRotations) do\n            display = \"Rotation \"..i..\": \";\n            for _,guy in pairs(rotation)do\n                display = display .. (aura_env.debug and guy or WA_ClassColorName(guy)) .. \" \";\n            end\n            print(display);\n        end\n        \n        for i,backup  in pairs(aura_env.vitaBackups) do\n            display = \"Backups \"..i..\": \";\n            for _,backupGuy in pairs(backup) do\n                display = display .. (aura_env.debug and backupGuy or WA_ClassColorName(backupGuy)) .. \" \";\n            end\n            print(display);\n        end\n        \n    end\n    \n    aura_env.initialized = true;\n    \n    --ViragDevTool_AddData(aura_env.vitaRotations,\"aura_env.vitaRotations\");\n    --ViragDevTool_AddData(aura_env.vitaBackups,\"aura_env.vitaBackups\");\nend\n\nlocal function hasVita(player)\n    local vitaName = aura_env.WA_GetUnitAura(player,aura_env.trackedSpell,\"HARMFUL\");\n    --local debugData = {GetTime(),\"hasVita\",player,aura_env.WA_GetUnitAura(player,aura_env.trackedSpell,\"HARMFUL\")};\n    --ViragDevTool_AddData(debugData,\"hasVita \"..player);\n    return vitaName;\nend\n\nlocal function hasInstability(player)\n    local instabilityName = aura_env.WA_GetUnitAura(player,aura_env.instabilitySpell,\"HARMFUL\");\n    --local debugData = {GetTime(),\"hasInstability\",player,aura_env.WA_GetUnitAura(player,aura_env.instabilitySpell,\"HARMFUL\")};\n    --ViragDevTool_AddData(debugData,\"hasInstability \"..player);\n    return instabilityName;\nend\n\nlocal function isValidVitaTarget(player)\n    return not UnitIsDead(player) and not hasVita(player) and not hasInstability(player);\nend\n\nlocal function isQueuedAsBackup(player)\n    for _,backup in pairs(aura_env.assignedBackups[aura_env.vitaCounter])do\n        if(player == backup) then return true; end;\n    end\n    return false;\nend\n\nlocal function getValidBackup(backups)\n    for _,backup in ipairs(backups) do\n        if(not isQueuedAsBackup(backup) and isValidVitaTarget(backup))then\n            return backup;\n        end\n    end\nend\n\n--check vita rotation, replace dead/wrong players with backups\nfunction aura_env.checkRotation(vitaCounter)\n    local players = aura_env.vitaRotations[vitaCounter];\n    local backups = aura_env.vitaBackups[vitaCounter];\n    \n    aura_env.assignedBackups[vitaCounter] = {};\n    \n    if(players)then\n        local player;\n        for i = 1+aura_env.maxStacks-aura_env.stacks,aura_env.maxStacks do\n            player = players[i];\n            if(not player or not isValidVitaTarget(player))then\n                aura_env.assignedBackups[vitaCounter][i] = getValidBackup(backups);\n            end\n        end\n    end\n    --ViragDevTool_AddData(aura_env.assignedBackups,\"aura_env.assignedBackups\");\nend\n\nfunction aura_env.getAssginedPlayer(vitaCounter,playerIdx)\n    if(aura_env.assignedBackups[vitaCounter] and aura_env.assignedBackups[vitaCounter][playerIdx])then\n        return aura_env.assignedBackups[vitaCounter][playerIdx];\n    end\n    if(aura_env.vitaRotations[vitaCounter] and aura_env.vitaRotations[vitaCounter][playerIdx])then\n        return aura_env.vitaRotations[vitaCounter][playerIdx];\n    end\n    return nil;\nend\n\nfunction aura_env.buildNameLists(isOnLeft)\n    local lists = {};\n    lists[0],lists[1] = \"\",\"\";\n    \n    local holder = aura_env.debug and aura_env.holder or WA_ClassColorName(aura_env.holder);\n    lists[isOnLeft and 0 or 1] = lists[isOnLeft and 0 or 1] .. holder .. \"\\n\";\n    for i = aura_env.stacks-1,0,-1 do\n        local name = aura_env.debug and aura_env.getAssginedPlayer(aura_env.vitaCounter,aura_env.maxStacks-i) or WA_ClassColorName(aura_env.getAssginedPlayer(aura_env.vitaCounter,aura_env.maxStacks-i))\n        local currentIndex = (i % 2 == (isOnleft and 0 or 1)) and 0 or 1;\n        lists[currentIndex] = lists[currentIndex] .. name .. \"\\n\"\n    end\n    \n    return lists[0],lists[1]\nend",
