rrentVita = true;\n                    end\n                    if(aura_env.vitaRotations[aura_env.vitaCounter] and aura_env.vitaRotations[aura_env.vitaCounter][aura_env.maxStacks-i] == WeakAuras.me)then\n                        aura_env.isInCurrentVita = true;\n                    end\n                end\n                \n                WeakAuras.ScanEvents(\"WA_RADEN_VITA_UPDATE\",vitaRunners,aura_env.holder);\n                \n                return true;\n                \n            elseif(spellId == aura_env.instabilitySpell and aura_env.stacks == 1)then\n                if(not aura_env.lastSent)then\n                    aura_env.lastSent = true;\n                    WeakAuras.ScanEvents(\"WA_RADEN_VITA_UPDATE\",{},nil);\n                end\n            end\n        end\n    elseif(event == \"GROUP_ROSTER_UPDATE\")then\n        aura_env.initialized = false;\n    end\nend\n\n\n\n",
