						["custom"] = "function(event,...)\n    \n    if(event == \"ENCOUNTER_START\")then\n        \n        if(not aura_env.initialized)then\n            aura_env.readRotationFromExrtNotes();\n        end\n        \n        aura_env.vitaCounter = 0;\n        aura_env.assignedBackups = {};\n        \n        local difficultyID = select(3,...);\n        if (difficultyID == 16)then\n            aura_env.duration = 6;\n        else\n            aura_env.duration = 7;\n        end\n        \n    elseif (event == \"READY_CHECK\") then\n        \n        aura_env.readRotationFromExrtNotes();\n        \n    elseif (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and aura_env.initialized) then\n        \n        local time,subevent,_,_,_,_,_,_,destName,_,_,spellId = ... \n        if subevent == \"SPELL_AURA_APPLIED\" then\n            if(spellId == aura_env.voidEmpowermentSpell)then\n                \n                aura_env.stacks = aura_env.maxStacks;\n                aura_env.isInCurrentVita = false;\n                aura_env.vitaCounter = aura_env.vitaCounter + 1;\n                aura_env.lastSent = nil;\n                aura_env.lastVoidHit = 0;\n                aura_env.lastRunners = {};\n                \n                --print(\"Start\");\n                aura_env.checkUpdateRotationAndSend(false);\n                \n                return true;\n                \n            elseif(spellId == aura_env.instabilitySpell and aura_env.vitaCounter)then\n                \n                local validHit = false;\n                local players = aura_env.vitaRotations[vitaCounter];\n                \n                if(players)then\n                    for _,player in pairs(players)do\n                        if(player == destName)then\n                            validHit = true;\n                            break;\n                        end\n                    end\n                end\n                \n                if(validHit)then\n                    --print(\"instability\",aura_env.stacks ~= 0)\n                    if(aura_env.stacks == 1)then\n                        if(not aura_env.lastSent)then\n                            aura_env.lastSent = true;\n                            WeakAuras.ScanEvents(\"WA_RADEN_VOID_UPDATE\",{});\n                        end\n                    elseif(aura_env.stacks ~= 0) then\n                        --print(\"Instability\");\n                        if(aura_env.checkUpdateRotationAndSend(true))then\n                            aura_env.region.subRegionEvents:Notify(\"Update\");\n                        end\n                    end\n                end\n            end\n            \n        elseif(spellId == aura_env.unstableVoidSpell and string.find(subevent,\"SPELL_\"))then\n            if(aura_env.lastVoidHit + 1 < time)then\n                aura_env.lastVoidHit = time;\n                \n                aura_env.stacks = aura_env.stacks - 1;\n                \n                if(aura_env.stacks > 0)then\n                    --print(\"VoidDmg\");\n                    aura_env.checkUpdateRotationAndSend(false);\n                    return true;\n                else\n                    aura_env.inCurrentVita = false;\n                end\n            end\n        end\n    elseif(event == \"GROUP_ROSTER_UPDATE\")then\n        aura_env.initialized = false;\n    end\nend\n\n\n\n\n\n",
