						["custom"] = "function(allstates, e, u,...)\n    if e == \"ENCOUNTER_START\" then\n        aura_env.counter = 0\n        local _,_,ID = GetInstanceInfo()\n        aura_env.deaths = (ID ~= 16 and 2) or (ID == 16 and 3)\n        aura_env.transition = (ID ~= 16 and 40) or (ID == 16 and 50)\n    end\n    if e == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" then\n        for _, state in pairs(allstates) do\n            state.show = false;\n            state.changed = true;\n        end\n        for i = 1,5 do\n            local unit = \"boss\"..i\n            local guid = UnitGUID(unit)\n            if guid and aura_env.guids[select(6, strsplit(\"-\", guid))] then\n                local state = allstates[\"energy\"]\n                local current = UnitPower(unit)\n                local healthpct = (UnitHealth(unit)/UnitHealthMax(unit))*100\n                if not state and current >= 85 and healthpct > aura_env.transition then\n                    allstates[\"energy\"] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"static\",\n                        energy = current,\n                        icon = 1391782,\n                        name = \"Hide Inc\"\n                    }\n                end\n            end\n        end\n    end\n    if e == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags = ...\n        if subEvent == \"UNIT_DIED\" then\n            local npcID = select(6, strsplit(\"-\", destGUID))\n            if npcID and (npcID == \"153091\" or npcID == \"153090\" or npcID == \"153092\") then\n                aura_env.counter = aura_env.counter + 1\n                local state = allstates[\"energy\"]\n                if aura_env.counter == aura_env.deaths and state then\n                    state.icon = 2065615\n                    state.name = \"Portal Inc\"\n      